import React from 'react';

import SelectProjectNum from './components/SelectProjectNum';
import ProjectDetail from './components/ProjectDetail';

import one from "./images/one.jpg"
import two from "./images/two.png"
import three from "./images/three.png"
import four from "./images/four.png"

class ShowProjectList extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            number : this.props.number || 4
        }
        this.onNumberSelect = this.onNumberSelect.bind(this);
    }
    onNumberSelect(num){
        console.log(num);
        this.number = num
    }
    render(){
        if(this.state.number === 4){

            return(
                <div>
                    <SelectProjectNum onNumberSelect={this.onNumberSelect}/>
                        <div className="body">
                            <ProjectDetail name="My Project One" image={one} className="profilepic" />
                            <ProjectDetail name="My Project Two" image={one} className="profilepic" />
                            <ProjectDetail name="My Project Three" image={one} className="profilepic" />
                            <ProjectDetail name="My Project Four" image={one} className="profilepic" />
                        </div>
                </div>
            )
        }else{
            return(
                <div>
                    <SelectProjectNum onNumberSelect={this.onNumberSelect}/>
                        <div className="body">
                            <ProjectDetail name="My Project One" image={one} className="profilepic" />
                            <ProjectDetail name="My Project Two" image={one} className="profilepic" />
                            <ProjectDetail name="My Project Three" image={one} className="profilepic" />
                            <ProjectDetail name="My Project Four" image={one} className="profilepic" />
                        </div>
                        <br/>
                        <div className="body">
                            <ProjectDetail name="My Project One" image={one} className="profilepic" />
                            <ProjectDetail name="My Project Two" image={one} className="profilepic" />
                            <ProjectDetail name="My Project Three" image={one} className="profilepic" />
                            <ProjectDetail name="My Project Four" image={one} className="profilepic" />
                        </div>
                </div>
            )
        }
    }
}

export default ShowProjectList;
