import { ReactComponent } from '../*.svg';
import SelectProjectNum from './components/SelectProjectNum';
import React from 'react';


class ShowProjectList extends ReactComponent{
    constructor(props){
        super(props);
        this.state = {
            number : this.props.number
        }
        this.onNumberSelect = this.onNumberSelect.bind(this);
    }
    onNumberSelect(num){
        this.setState({
             number : num 
        });
    }
    render(){
        if(this.number === 4){
            return <p>4</p>
        }else{
            return <p>8</p>
        }
    }
}

export default ShowProjectList;
