import React from 'react';
import profilepic from "./images/profilepic.png"

import one from "./images/one.jpg"
import two from "./images/two.png"
import three from "./images/three.png"
import four from "./images/four.png"

import './App.css';
import Objective from './components/Objective';
import Detail from './components/Detail';
import SelectProjectNum from './components/SelectProjectNum';

// 1.  use separation of concern
// 2. show project detils dynamically
// 3. make reusable component


function SelectComponent() {
  return (
    <div>
      {/* Use this value to render project dynamically. */}
      <select onChange={(e) => console.log("Value", e.target.value)}>
        <option value="4">4</option>
        <option value="8">8</option>
      </select>
    </div>
  );
}

function ProjectDetails(props) {
  return (
    <div>
      <h4> {props.name} </h4>
      <img src={props.image} alt="logo" className={props.className} />
    </div>
  );
}

// const project1 = <projectDetails name="one" image={one}/>

function App() {
  return (
    <div className="App">
      <div className="header">
        <img src={profilepic} className="profilepic" alt="logo" />
        <div className="details">
          <Detail name="Prosen Ghosh" />
          <Detail name="Software Engineer"  />
          <Detail name="Brain Station 23" />
        </div>
      </div>
      <Objective text="HI i am a software engineer brain station 23. i have done some project. The projects are shown below." />
      <SelectProjectNum />
      <div className="body">
        <ProjectDetails name="logo" image={one} className="profilepic" />

        <div>
          <h4> This is a demo Project Done by me </h4>
          <img src={two} className="profilepic" alt="logo" />
        </div>
        <div>
          <h4> This is a demo Project Done by me </h4>
          <img src={three} className="profilepic" alt="logo" />
        </div>
        <div>
          <h4> This is a demo Project Done by me </h4>
          <img src={four} className="profilepic" alt="logo" />
        </div>
      </div>
    </div>
  );
}




export default App;
