import React from "react";

import unsplash from "../api/unsplash";

import SearchBar from "./SearchBar";
import ImageList from "./ImageList";

// Convert this class to functional component.
// Use hooks to manage it's local state.
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      images: [],
      error_msg: null,
    };
  }
  // Handle Error
  onSearchSubmit = (term) => {
    // const response = await unsplash.get("/search/photos", {
    //   params: {
    //     query: term,
    //   },
    // });

    fetch("https://api.unsplash.com/search/photos/?query=" + term, {
      method: "GET",
      headers: {
        Authorization: "Client-ID R4Q_V32stbrWOzb3DpOntSfstan64mljhm-QftIjSC",
      },
    })
      .then(async (r) => await r.json())
      .then((r) =>
        this.setState({
          images: r.results,
        })
      )
      .catch((e) =>
        this.setState({
          error_msg: e,
        })
      );

    console.log(this.state.error_msg);
  };
  render() {
    return (
      <div className="ui container" style={{ marginTop: "10px" }}>
        <SearchBar onSearchSubmit={this.onSearchSubmit} />
        <h4>Found: {this.state.images.length} Images</h4>
        <ImageList images={this.state.images} />
      </div>
    );
  }
}

export default App;
