import React from 'react';
import { useLocation, useParams } from 'react-router-dom';
import { useQuery, QueryCache, ReactQueryCacheProvider } from 'react-query';

const Detail = ({ image }) => {




    const { isLoading, error, data } = useQuery(['Image'], async (key) => {


        const response = await fetch(`https://api.unsplash.com/photos/${useParams().id}`, {
            method: "GET",
            headers: {
                Authorization: "Client-ID R4Q_V32stbrWOzb3DpOntSfstan64mljhm-QftIjSCY",
            },
        });
        return response.json();
    }
    );



    console.log(useParams().id);
    return (
        <div className="ui container" >
            <div>
                <img width="502px" src={image.urls.regular} alt="img"></img>
                <p style={{ border: "2px solid black", padding: '10px' }}> {image.description || "Here is the description of the image"} </p>
            </div>
        </div>
    )

}

export default Detail;
