import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useQuery } from 'react-query';

const Detail = () => {


    const [image, setImage] = useState({});

    const id = useParams().id;


    const { isLoading, error, data } = useQuery(['Image'], async (key, params) => {
        const response = await fetch(`https://api.unsplash.com/photos/${id}`, {
            method: "GET",
            headers: {
                Authorization: "Client-ID R4Q_V32stbrWOzb3DpOntSfstan64mljhm-QftIjSCY",
            },
        });
        return response.json();
    }
    );


    useEffect(() => {
        if (!isLoading) {
            setImage(data);
            console.log(data);
            console.log(image);
            console.log(isLoading);
        }

    }, [isLoading, data, image])

    if (error) {
        return <div>Some Error</div>
    }

    if (isLoading) {
        return <div>Loading...</div>
    }
    return (
        // null
        <div className="ui container" >
            <div>
                <img width="502px" src={image.urls.regular} alt="img"></img>
                <p style={{ border: "2px solid black", padding: '10px' }}> {image.description || "Here is the description of the image"} </p>
            </div>
        </div>
    )

}

export default Detail;
