import React, { useState } from "react";
import { useQuery, QueryCache, ReactQueryCacheProvider } from 'react-query'

import SearchBar from "./SearchBar";
import ImageList from "./ImageList";

const queryCache = new QueryCache()



function content(term) {
  const { isLoading, error, data } = useQuery('repoData', () =>
    fetch('https://api.github.com/repos/tannerlinsley/react-query').then(res =>
      res.json()
    )
  )

  if (isLoading) return 'Loading...'

  if (error) return 'An error has occurred: ' + error.message

  return (
    <div>
      <h1>{data.name}</h1>
      <p>{data.description}</p>
      <strong>ğŸ‘€ {data.subscribers_count}</strong>{' '}
      <strong>âœ¨ {data.stargazers_count}</strong>{' '}
      <strong>ğŸ´ {data.forks_count}</strong>
    </div>
  )
}


const App = ({ setImage }) => {
  const [images, setImages] = useState([]);
  const [error_msg, setErrorMsg] = useState("");

  const onSearchSubmit = (term) => {


    fetch("https://api.unsplash.com/search/photos/?query=" + term, {
      method: "GET",
      headers: {
        Authorization: "Client-ID R4Q_V32stbrWOzb3DpOntSfstan64mljhm-QftIjSCY",
      },
    })
      .then((response) => {
        console.log(response);
        if (response.status === 200) {
          // console.log("here");
          return response.json();
        } else throw "Something went wrong";
      })
      .then((response) => {
        setImages(response.results);
      })
      .catch((err) => {
        setErrorMsg(err);
      });
  };

  const renderContent = () => {
    if (!error_msg) {
      return <ImageList images={images} setImage={setImage} />;
    } else {
      return (
        <h2
          style={{
            justifyContent: "center",
            alignItems: "center",
            display: "flex",
            color: "red",
          }}
        >
          {error_msg}
        </h2>
      );
    }
  };

  return (
    <ReactQueryCacheProvider queryCache={queryCache}>
      <div className="ui container" style={{ marginTop: "10px" }}>
        <SearchBar onSearchSubmit={onSearchSubmit} />
        <h4>Found: {images.length} Images</h4>
        {renderContent()}
      </div>
    </ReactQueryCacheProvider>
  );
};

export default App;
