import React from "react";

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams,
  useRouteMatch
} from "react-router-dom";


import Detail from './Detail';

const ImageList = (props) => {

  let { path, url } = useRouteMatch();


  const images = props.images.map((image) => {
    return (
      <div className="ui container">
      <Link to={`detail/${url}/${image.id}`}>  <img src={image.urls.regular} key={image.id} alt={image.description} /> </Link>
      <Switch>
          <Route path={`${url}/${image.id}`}>
            {/* <Detail imageSrc={image.urls.regular} description={image.description}/> */}
            <h1> Hi </h1>

          </Route>
        </Switch>

        </div>
    );
  });

  return <div style={{ width: "150px", height: "150px" }}> {images} </div>;
};

export default ImageList;
