import React from "react";

import unsplash from "../api/unsplash";

import SearchBar from "./SearchBar";
import ImageList from "./ImageList";

// Convert this class to functional component.
// Use hooks to manage it's local state.
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      images: [],
      error_msg: null,
    };
  }
  // Handle Error
  onSearchSubmit = async (term) => {
    // const response = await unsplash.get("/search/photos", {
    //   params: {
    //     query: term,
    //   },
    // });

    const response = await fetch(
      "https://api.unsplash.com/search/photos/?query=" + term,
      {
        method: "GET",
        headers: {
          Authorization:
            "Client-ID R4Q_V32stbrWOzb3DpOntSfstan64mljhm-QftIjSCY",
        },
      }
    )
      .then((r) => r.json())
      .catch((e) => (this.state.error_msg = e));
    this.setState({
      images: response.results,
    });
  };
  render() {
    return (
      <div className="ui container" style={{ marginTop: "10px" }}>
        <SearchBar onSearchSubmit={this.onSearchSubmit} />
        <h4>Found: {this.state.images.length} Images</h4>
        <ImageList images={this.state.images} />
      </div>
    );
  }
}

export default App;
